Sentiment.map:
function (doc) {
  emit(doc._id, {tweet: doc.tweet, sentiment: doc.sentiment, geo: doc.geo});
}

Sentiment+Date.map:
function (doc) {
  emit(doc._id, {tweet: doc.tweet, sentiment: doc.sentiment, geo: doc.geo, time: new Date(doc.created_at).getTime()});
}

Data.map:
function (doc) {
  var sentiment = doc.sentiment[0];
  var confidence = doc.sentiment[1];
  var suburb = doc.suburb;
  if (sentiment === "Positive" && confidence > 0.8 && suburb !== "") {
    emit([suburb, new Date(doc.created_at).getTime()], 1);
  } else if (sentiment === "Negative" && confidence > 0.8 && suburb !== "") {
    emit([suburb, new Date(doc.created_at).getTime()], 0);
  }
}
